import { useState, useEffect } from 'react';
import axios from 'axios';
import { getCurrentUserPlaylists, getPlaylistItems, getUserPlaylists } from '../spotify';
import { catchErrors } from '../utils';
//import { ChangeContext } from '../contexts/context';

//figure out username
export const MergePlaylists = (userName) => {
    //await only works if function applied to returns a promise... read on this
    //const CurrentSongList = CurrentPlaylists();
    //const UserSongList = UserPlaylists("lauravonbargen");
    const merge = async () => {
        console.log("top");
        const CurrentSongList = await new Promise((resolve, reject) => {
            const cur = CurrentPlaylists();
            resolve(cur);
        });

        const UserSongList = await new Promise((resolve, reject) => {
            const usr = UserPlaylists(userName);
            resolve(usr);
        });

        findOverlap(CurrentSongList, UserSongList);
        console.log("here");
    }
}

function findOverlap(CurrentSongs, UserSongs) {
    let overlap = [];

    for (var i = 0; i < UserSongs.length; i++) {
        const index = CurrentSongs.indexOf(UserSongs[i]);
        if (index === -1) {
            //means song is unique and should be added to overlap
            overlap.push(CurrentSongs[index]);
        }
    }
    console.log("Overlap: " + overlap.length);
}

const CurrentPlaylists = () => {
  const [playlistsData, setPlaylistsData] = useState(null);
  const [playlists, setPlaylists] = useState(null);

  const PIDS = [];
  const songIDs = [];


  useEffect(() =>  {
    const fetchData = async () => {
      const { data } = await getCurrentUserPlaylists();
      
      setPlaylists(playlists => ([
        ...playlists ? playlists : [],
        ...data.items
      ]));

      setPlaylistsData(data);
    };
    //console.log(playlistsData);
    catchErrors(fetchData());
  }, []);

  // When playlistsData updates, check if there are more playlists to fetch
  // then update the state variable
  useEffect(() => {
    if (!playlistsData) {
      return;
    }

    // Playlist endpoint only returns 20 playlists at a time, so we need to
    // make sure we get ALL playlists by fetching the next set of playlists
    const fetchMoreData = async () => {
      if (playlistsData.next) {
        //as long as there is more data to be retrieved
        const { data } = await axios.get(playlistsData.next);
        
        setPlaylists(playlists => ([
            ...playlists ? playlists : [],
            ...data.items
          ]));

        setPlaylistsData(data);
      } else {
        //when all urls are retrieved
        if(playlists !== null) { 
            Object.entries(playlists).forEach((key, value) => {
                const PID = key[1]["id"];
                //console.log(PID);
                if (!PIDS.includes(PID)) { 
                    PIDS.push(PID);
                }
            });
            
            for (var PID in PIDS) {
                const id = PIDS[PID];
                
                //data contains first 100 songs
                let listData = await getPlaylistItems(id);
                //console.log(listData);
                
                Object.entries(listData.data.items).forEach((key, value) => {
                    const songID = key[1]["track"]["id"];
                    if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                });
                //console.log("before while: " + i);
                songIDs.push(listData.data.items);
                
                while (listData.data.next) {

                    listData = await axios.get(listData.data.next);
                    console.log("nexted");
                    
                    Object.entries(listData.data.items).forEach((key, value) => {
                        const songID = key[1]["track"]["id"];
                        if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                    })
                    songIDs.push(listData.data.items);
                    
                }
            }
            console.log(songIDs.length);
        }
      }
    };
    
    catchErrors(fetchMoreData());

  }, [playlistsData]);


  return songIDs;
};

const UserPlaylists = (userName) => {
    const [playlistsData, setPlaylistsData] = useState(null);
    const [playlists, setPlaylists] = useState(null);

    const PIDS = [];
    const songIDs = [];

    useEffect(() =>  {
    const fetchData = async () => {
        const { data } = await getUserPlaylists(userName);
        
        setPlaylists(playlists => ([
        ...playlists ? playlists : [],
        ...data.items
        ]));

        setPlaylistsData(data);
    };
    catchErrors(fetchData());
    }, []);


    useEffect(() => {
        if (!playlistsData) {
            return;
        }

        // Playlist endpoint only returns 20 playlists at a time, so we need to
        // make sure we get ALL playlists by fetching the next set of playlists
        const fetchMoreData = async () => {
            if (playlistsData.next) {
            //as long as there is more data to be retrieved
            const { data } = await axios.get(playlistsData.next);
            
            setPlaylists(playlists => ([
                ...playlists ? playlists : [],
                ...data.items
                ]));

            setPlaylistsData(data);
            } else {
            //when all urls are retrieved
            if(playlists !== null) { 
                Object.entries(playlists).forEach((key, value) => {
                    const PID = key[1]["id"];
                    //console.log(PID);
                    if (!PIDS.includes(PID)) { 
                        PIDS.push(PID);
                    }
                });
                
                for (var PID in PIDS) {
                    const id = PIDS[PID];
                    
                    //data contains first 100 songs
                    let listData = await getPlaylistItems(id);
                    //console.log(listData);
                    
                    Object.entries(listData.data.items).forEach((key, value) => {
                        const songID = key[1]["track"]["id"];
                        if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                    });
                    //console.log("before while: " + i);
                    songIDs.push(listData.data.items);
                    
                    while (listData.data.next) {

                        listData = await axios.get(listData.data.next);
                        console.log("nexted");
                        
                        Object.entries(listData.data.items).forEach((key, value) => {
                            const songID = key[1]["track"]["id"];
                            if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                        })
                        songIDs.push(listData.data.items);
                        
                    }
                }
                console.log(songIDs.length);
            }
            }
        };
        
        catchErrors(fetchMoreData());

        }, [playlistsData]);

        return songIDs;
};
