import logo from './logo.svg';
import { catchErrors } from './utils';
import { useEffect, useState } from 'react';
import { accessToken, logout, getCurrentUserProfile, getCurrentUserPlaylists } from './spotify';
import { getCurrentUserSavedTracks } from './spotify';
import './App.css';
import axios from 'axios';

function App() {

  //useState hook
  const [token, setToken] = useState(null);
  const [profile, setProfile] = useState(null);
  const [savedTracks, setSavedTracks] = useState(null);

  useEffect(() => {
    //assign token to imported access token
    setToken(accessToken);

    const fetchData = async() => {
      const { data } = await getCurrentUserProfile();
      setProfile(data);
    }

    catchErrors(fetchData());

  }, [])


  //contains paging obj w/list of 20 playlists
  const [playlistsData, setPlaylistsData] = useState(null);
  //contains playlist data
  const [playlists, setPlaylists] = useState(null);

  //get first 20 playlists
  useEffect(() => {
    const fetchData = async() => {
      const { data } = await getCurrentUserPlaylists();
      setPlaylistsData(data);
    };

    catchErrors(fetchData());
  }, []);

  //if more than 20, get next param url
  useEffect(() => {
    if (!playlistsData) { return; }

    //get next 20 playlists
    const fetchNextData = async () => {
      if (playlistsData.next) {
        const { data } = await axios.get(playlistsData.next);
        setPlaylistsData(data); 
      }
    }

    //+= playlists
    setPlaylists((playlists) => [
      ...(playlists ? playlists : []),
      ...playlistsData.items
    ]);

    catchErrors(fetchNextData());
  }, [playlistsData]);

  return (
    <div className="App">
      <header className="App-header">
        {!token ? (
          <a
          className="App-link"
          href="http://localhost:8888/login"
        >
          Log in to Spotify
        </a>
        ) : (
          <h1>This is a logged in header</h1>
        )}
      </header>
    </div>
  );
}

export default App;
