    const fetchData = async () => {
        const { data } = await getUserPlaylists(userName);
        
        setPlaylists(playlists => ([
        ...playlists ? playlists : [],
        ...data.items
        ]));

        setPlaylistsData(data);
    };
    catchErrors(fetchData());
    }, []);


    useEffect(() => {
        if (!playlistsData) {
            return;
        }

        // Playlist endpoint only returns 20 playlists at a time, so we need to
        // make sure we get ALL playlists by fetching the next set of playlists
        const fetchMoreData = async () => {
            if (playlistsData.next) {
            //as long as there is more data to be retrieved
            const { data } = await axios.get(playlistsData.next);
            
            setPlaylists(playlists => ([
                ...playlists ? playlists : [],
                ...data.items
                ]));

            setPlaylistsData(data);
            } else {
            //when all urls are retrieved
            if(playlists !== null) { 
                Object.entries(playlists).forEach((key, value) => {
                    const PID = key[1]["id"];
                    //console.log(PID);
                    if (!PIDS.includes(PID)) { 
                        PIDS.push(PID);
                    }
                });
                
                for (var PID in PIDS) {
                    const id = PIDS[PID];
                    
                    //data contains first 100 songs
                    let listData = await getPlaylistItems(id);
                    //console.log(listData);
                    
                    Object.entries(listData.data.items).forEach((key, value) => {
                        const songID = key[1]["track"]["id"];
                        if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                    });
                    //console.log("before while: " + i);
                    songIDs.push(listData.data.items);
                    
                    while (listData.data.next) {

                        listData = await axios.get(listData.data.next);
                        console.log("nexted");
                        
                        Object.entries(listData.data.items).forEach((key, value) => {
                            const songID = key[1]["track"]["id"];
                            if (!songIDs.includes(songID) && songID !== null) { songIDs.push(songID); }
                        })
                        songIDs.push(listData.data.items);
                        
                    }
                }
                
                //return songIDs;
            }
            }
        };
        
        catchErrors(fetchMoreData());
        return songIDs;

        }, [playlistsData]);
};
