import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { getCurrentUserPlaylists, getPlaylistItems, getUserPlaylists, createPlaylist } from '../spotify';
import { catchErrors } from '../utils';
//import { ChangeContext } from '../contexts/context';

