import logo from './logo.svg';
import { catchErrors } from './utils';
import { useContext, useEffect, useState } from 'react';
import { accessToken, logout, getCurrentUserProfile, getCurrentUserPlaylists } from './spotify';
import { getCurrentUserSavedTracks } from './spotify';
import './App.css';
import axios from 'axios';
import { Playlists, setStatus, status } from './dataCollecting/playlists';
import { 
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation
 } from "react-router-dom";

import { ChangeContext } from './contexts/context';

function App() {


  //useState hook
  const [token, setToken] = useState(null);
  const [profile, setProfile] = useState(null);
  const [savedTracks, setSavedTracks] = useState(null);

  useEffect(() => {
    //assign token to imported access token
    setToken(accessToken);

    const fetchData = async() => {
      const { data } = await getCurrentUserProfile();
      setProfile(data);
    }

    catchErrors(fetchData());

  }, [])


  //contains paging obj w/list of 20 playlists
  const [playlistsData, setPlaylistsData] = useState(null);
  //contains playlist data
  const [playlists, setPlaylists] = useState(null);

  //setPlaylists(Playlists);

  //console.log(playlists.length);

  return (
    <div className="App">
      <header className="App-header">
        {!token ? (
          <a
          className="App-link"
          href="http://localhost:8888/login"
        >
          Log in to Spotify
        </a>
        ) : (
          <Router>
            <nav>
              <a href="/">Home</a>
              <a href="/by-user">By User</a>
              <a href="/by-playlist">By Playlist</a>
            </nav>

            <Routes>
              <Route path="/by-user" element={<UserSearch/>}/>
              <Route path="/by-playlist" element={<URLSearch/>}/>
              <Route path="/" element={<Home/>}/>
            </Routes>
          </Router>
        )}
      </header>
    </div>
  );
}

function UserSearch() {
  return <h1>User Search!</h1>;
}

function URLSearch() {
  return (
    <>
      <h1>URL Search!</h1>
      <button onClick={StatusChange}>Create</button>
    </>
  );
}

function Home() {
  return (
    <>
      <h1>Home!</h1>
      <button onClick={logout}>Log out</button>
    </>
  );
}

const StatusChange = () => {
  const {didChange, setDidChange} = useContext(ChangeContext);
  if (didChange === 0) { setDidChange(1); }
  else if (didChange === 1) { setDidChange(0); }
  return;
}

export default App;
