import { useState, useEffect } from 'react';
import axios from 'axios';
import { getCurrentUserPlaylists, getPlaylistItems } from '../spotify';
import { catchErrors } from '../utils';
//import { ChangeContext } from '../contexts/context';


export const Playlists = () => {
  const [playlistsData, setPlaylistsData] = useState(null);
  const [playlists, setPlaylists] = useState(null);
  const [playlistCount, setPlaylistCount] = useState(0);

  useEffect(() =>  {
    const fetchData = async () => {
      const { data } = await getCurrentUserPlaylists();
      setPlaylistsData(data);
    };
    //console.log(playlistsData);
    catchErrors(fetchData());
  }, []);

  // When playlistsData updates, check if there are more playlists to fetch
  // then update the state variable
  useEffect(() => {
    if (!playlistsData) {
      return;
    }

    // Playlist endpoint only returns 20 playlists at a time, so we need to
    // make sure we get ALL playlists by fetching the next set of playlists
    const fetchMoreData = async () => {
      if (playlistsData.next) {
        const { data } = await axios.get(playlistsData.next);
        setPlaylistsData(data);
      }
    };
    
    // Use functional update to update playlists state variable
    // to avoid including playlists as a dependency for this hook
    // and creating an infinite loop
    setPlaylists(playlists => ([
      ...playlists ? playlists : [],
      ...playlistsData.items
    ]));
    console.log(playlistsData.items.length);
    setPlaylistCount(playlistsData.items.length);
    // usually will not get to here
    catchErrors(fetchMoreData());

  }, [playlistsData]);

  const URLS = [];
  const [didFinish, setDidFinish] = useState(false);

  //extract playlist urls into URLS array
  useEffect(() => {
    if(playlists !== null) { 
        //console.log(playlists);
        Object.entries(playlists).forEach((key, value) => {
            const url = key[1]["external_urls"]["spotify"];
            if (!URLS.includes(url)) { 
                URLS.push(url);
            }
        });
    }
    console.log('-----------------------------');
  }, [playlists]);

  //for each playlist, retrieve song urls
  const songs = [];
  useEffect(() => {
    const fetchData = async () => {
        //logic
    }
  });

  return;
};
